import React from "react";

class PersonCard extends React.Component {

    constructor (props){
        super (props);
        this.state = { 
            age:this.props.age
        }


    }

    cumpleaños = () => {
        this.setState ({age: this.state.age+1})
    }

    render (){
        const {FirstName, LastName,age,Color} = this.props;

        return (

            <div class className = "card" >
                <h4>{FirstName} {LastName}</h4>
                <p><b>Edad:</b>{this.state.age}</p>
                <p><b>Hair Color:</b>{Color}</p>
                <button className ="btn btn-info" onClick={this.cumpleaños}>Boton de cumpleaños {FirstName}</button>
            </div>
        )

    }

}

export default PersonCard;
